class Solution {
    public List<Integer> largestDivisibleSubset(int[] nums) {
    	Arrays.sort(nums);
        List<Integer> longestList = new ArrayList<Integer>();
        List<Integer> localList = new ArrayList<Integer>();
        if(nums.length == 0)
        	return new ArrayList<Integer>();
        for(int i = 0; i < nums.length; i++){
        	if(nums[i] != 0){
	        	localList.clear();
	        	localList.add(nums[i]);
	      		jLoop:
	        	for(int j = 0; j < nums.length; j++){
	        		if(nums[j] == 0)
	        			continue;
	        		for(int k = 0; k < localList.size(); k++){
	        			if(nums[j] % localList.get(k) != 0 && localList.get(k) % nums[j] != 0)
	        				continue jLoop;
	        		}
	        		localList.add(nums[j]);
	        	}
	        	if(localList.size() > longestList.size())
	        		longestList = new ArrayList<Integer>(localList);       	
	        }	
        }
        longestList.remove(0);
        return longestList;
    }
}
